export const formatProductForSegment = (product, quantity = 1, position = null) => {
  return {
    product_id: product.id,
    sku: product.sku,
    category: product.category,
    name: product.name,
    brand: product.brand,
    variant: product.variant,
    price: product.price,
    quantity: quantity,
    // coupon: product.coupon, // Add if coupons are implemented per product
    position: position,
    url: `${window.location.origin}${window.location.pathname}#/product/${product.id}`, // Dynamic URL
    image_url: product.imageUrl,
  };
};
